mapper.select:
SELECT
ID,

FROM
T_ASSET

	
		AND asset_type_id in
		
			#{item}
		
	
	
		AND rent = #{rent}
	
	
		AND province_id = #{provinceId}
	
	
		AND city_id = #{cityId}
	

ORDER BY CREATE_DATE DESC

SELECT t.asset_type_id, t.code, t.shop_id, t.province_id, t.city_id, t.use_time, t.rent, t.location, u.contract_id, u.rent_person_type, u.comp_person_name, u.detail_location FROM t_asset t LEFT JOIN (SELECT c.contract_id, c.send_date, c.back_date, r.asset_id, o.rent_person_type, o.name AS comp_person_name, o.detail_location FROM t_contract c, (SELECT o1.id AS order_id, o1.rent_person_type, NAME, o1.detail_location FROM t_rent_company_info m, t_order o1 WHERE o1.rent_person_type = 1 AND rent_company_id = m.id UNION SELECT o2.id AS order_id,o2.rent_person_type, NAME, o2.detail_location FROM t_rent_person_info p, t_order o2 WHERE o2.rent_person_type = 0 AND o2.rent_person_id = p.id) o, t_asset_manager_rent_asset r WHERE r.order_id = o.order_id AND c.order_id = o.order_id) u ON t.id = u.asset_id
AND asset_type_id in
#{item}
AND rent = #{rent}
AND province_id = #{provinceId}
AND city_id = #{cityId}
ORDER BY u.contract_id,operate_date DESC
